public static boolean checkSSO(java.lang.String strUserId, java.lang.String strSessionId, java.lang.String strToken, DataSource ds) throws java.lang.Exception {
    boolean retVal = false;
    java.sql.Connection conn = null;
    try {
        conn = ds.getConnection();
        java.sql.CallableStatement cstmt = conn.prepareCall("{call Sap_Check_Token(?, ?, ?, ?)}");
        cstmt.setString(1, strUserId);
        cstmt.setString(2, strSessionId);
        cstmt.setString(3, strToken);
        cstmt.setString(4, "");
        cstmt.registerOutParameter(3, java.sql.Types.VARCHAR);
        cstmt.execute();
        if (cstmt.getString(3).equals("1")) {
            retVal = true;
        }
    } catch (java.lang.Exception e) {
        throw new java.lang.Exception("--->checkSSO(): " + e.getMessage() + ":" + e.getCause());
    } finally {
        if (conn != null) {
            conn.close();
        }
    }
    return retVal;
}

